INSERT INTO cmsosc.posts (categoria_id, imagem, titulo, descricao, arquivo, data, status, resumida, cmsuser_id, created_at, updated_at) VALUES (2,  '9825-tdinfograficos.jpg', 'Governo Federal e as OSCs: Recursos públicos e parcerias', '<p>Das 820 mil organiza&ccedil;&otilde;es da sociedade civil (OSCs) em atividade no Brasil, 22 mil (2,7%) receberam recursos federais entre 2010 e 2018. Foi repassado a essas entidades um total de R$ 118,5 bilh&otilde;es nesse per&iacute;odo, o equivalente a 0,5% do or&ccedil;amento anual da Uni&atilde;o.&nbsp;Os dados fazem parte de um levantamento realizado pelo Instituto de Pesquisa Econ&ocirc;mica Aplicada (Ipea), respons&aacute;vel pelo Mapa das Organiza&ccedil;&otilde;es da Sociedade Civil, e s&atilde;o resultado do cruzamento das informa&ccedil;&otilde;es do Mapa e da execu&ccedil;&atilde;o or&ccedil;ament&aacute;ria federal.&nbsp;</p>
<p>&nbsp;</p>
<h4 class="media-heading">An&aacute;lise&nbsp;completa</h4>
<p><a href="/cms/arquivos/analises/2624-tdreduzido.pdf"><img src="/img/botao_pdf.png" width="35" height="35" />Baixar a publica&ccedil;&atilde;o - PDF</a></p>', '2624-tdreduzido.pdf', '2019-07-01', 1, 'Estudo inédito realizado pela equipe do Mapa das OSCs revelou, entre outros pontos, que das 820 mil organizações da sociedade civil (OSCs) em atividade no Brasil, 22 mil (2,7%) receberam recursos federais entre 2010 e 2018', 3, '2019-09-25 21:31:31', '2019-10-08 13:30:04');
INSERT INTO cmsosc.posts (categoria_id, imagem, titulo, descricao, arquivo, data, status, resumida, cmsuser_id, created_at, updated_at) VALUES (2,  '5623-ososcip.jpg', 'Relatório do Mapa das OSCs traça o perfil das OSs e OSCIPs em atividade no Brasil', '<p style="text-align: justify;">Desde o fim da d&eacute;cada de 1990, os t&iacute;tulos de Organiza&ccedil;&atilde;o Social (OS) ou Organiza&ccedil;&atilde;o da Sociedade Civil de Interesse P&uacute;blico (OSCIP) s&atilde;o outorgados a organiza&ccedil;&otilde;es da sociedade civil (OSCs) pela Administra&ccedil;&atilde;o P&uacute;blica municipal, estadual ou federal. O objetivo &eacute; garantir juridicamente que a entidade possa receber benef&iacute;cios de governos e &oacute;rg&atilde;os p&uacute;blicos (na forma de estabelecimento de parcerias, dota&ccedil;&otilde;es or&ccedil;ament&aacute;rias, isen&ccedil;&otilde;es fiscais etc.) para a realiza&ccedil;&atilde;o de suas atividades, que devem ser, necessariamente, de interesse coletivo da sociedade.</p>
<p style="text-align: justify;">Atualmente, h&aacute; 781.921 OSCs formais, ou seja, com Cadastro Nacional de Pessoa Jur&iacute;dica (CNPJ), em atividade no Brasil. Dessas, 1.114 s&atilde;o OSs e 7.046 s&atilde;o OSCIPs. Pensando em descrever melhor esse universo, o Mapa das OSCs produziu um relat&oacute;rio que tra&ccedil;a o perfil dessas entidades. O documento re&uacute;ne informa&ccedil;&otilde;es como: localiza&ccedil;&atilde;o geogr&aacute;fica, &aacute;rea de atua&ccedil;&atilde;o, v&iacute;nculos de trabalho, repasses federais etc., com base em dados provenientes de bases da&nbsp;Secretaria da Receita Federal (SRF), da Rela&ccedil;&atilde;o Anual de Informa&ccedil;&otilde;es Sociais (RAIS) e do Or&ccedil;amento P&uacute;blico Federal. &nbsp;</p>
<p style="text-align: justify;">&nbsp;</p>
<h4 class="media-heading">Publica&ccedil;&atilde;o completa</h4>
<p><a href="/cms/arquivos/analises/7883-relatorioososcipfinal.pdf"><img src="/img/botao_pdf.png" width="35" height="35" />Baixar a publica&ccedil;&atilde;o - PDF</a></p>', '7883-relatorioososcipfinal.pdf', '2020-07-23', 1, 'Documento reúne informações como: localização geográfica, área de atuação, vínculos de trabalho, repasses federais etc., a partir de dados provenientes de bases da SRF, RAIS e do Orçamento Público Federal', 3, '2020-07-23 19:45:17', '2020-08-07 16:47:58');