\COPY ( SELECT To_char(cd_identificador_osc, '00000000000000') AS cnpj, tx_razao_social_osc                             AS razao_social, tx_nome_fantasia_osc                            AS nome_fantasia, cd_natureza_juridica_osc                        AS natureza_juridica, tx_nome_natureza_juridica                       AS nat_jur_dessc, dt_fundacao_osc                                 AS data_inicio, Extract(year FROM dt_fundacao_osc)              AS ano_inicio, div_cnae20_corrigido                            AS cnae_divisao_corrigido, grp_cnae20_corrigido                            AS cnae_grupo_corrigido, clas_cnae20_corrigido                           AS cnae_classe_corrigido, tx_nome_classe_atividade_economica              AS classe_desc, sbcl_cnae_corrigido                             AS cnae_subclase_corrigido, tx_nome_subclasse_atividade_economica           AS subclasse_desc, tx_endereco                                     AS endereco, tx_bairro                                       AS bairro, nr_cep                                          AS cep, codemun                                         AS codemun, edmu_nm_municipio                               AS municipio, eduf_sg_uf                                      AS uf, LEFT(tx_telefone, 2)                            AS ddd, Substr(tx_telefone, 3)                          AS telefone, tx_email                                        AS email, tx_nome_area_atuacao, tx_nome_subarea_atuacao, qtd_vinc_ativos                                 AS n_vinc_ativos, qtd_portador_defic                              AS n_vinc_defic FROM   osc.tb_osc a JOIN osc.tb_dados_gerais b ON a.id_osc = b.id_osc JOIN syst.dc_natureza_juridica c ON b.cd_natureza_juridica_osc = c.cd_natureza_juridica LEFT JOIN graph.tb_rais d ON a.cd_identificador_osc = d.id_estab AND d.ano_rais = 2015 LEFT JOIN syst.dc_classe_atividade_economica e ON d.clas_cnae20_corrigido = e.cd_classe_atividade_economica LEFT JOIN syst.dc_subclasse_atividade_economica f ON d.sbcl_cnae_corrigido = Cast(f.cd_subclasse_atividade_economica AS TEXT) LEFT JOIN osc.tb_localizacao g ON a.id_osc = g.id_osc LEFT JOIN spat.ed_municipio h ON g.cd_municipio = h.edmu_cd_municipio LEFT JOIN spat.ed_uf i ON h.eduf_cd_uf = i.eduf_cd_uf LEFT JOIN osc.tb_contato j ON a.id_osc = j.id_osc LEFT JOIN osc.tb_area_atuacao l ON a.id_osc = l.id_osc LEFT JOIN syst.dc_area_atuacao m ON l.cd_area_atuacao = m.cd_area_atuacao LEFT JOIN syst.dc_subarea_atuacao n ON l.cd_subarea_atuacao = n.cd_subarea_atuacao WHERE  a.id_osc NOT IN ( 789809 ) AND a.bo_osc_ativa = true) TO 'planilhao_2022.csv' WITH (FORMAT CSV, HEADER, DELIMITER ';');
