\COPY (Select a.id_osc, a.cd_identificador_osc, c.tx_nome_fonte_recursos_osc, dt_ano_recursos_osc, nr_valor_recursos_osc, ft_fonte_recursos_osc, tx_razao_social_osc FROM osc.tb_osc a join osc.tb_recursos_osc b on a.id_osc = b.id_osc join syst.dc_fonte_recursos_osc c on b.cd_fonte_recursos_osc = c.cd_fonte_recursos_osc left join syst.dc_origem_fonte_recursos_osc d on c.cd_origem_fonte_recursos_osc = d.cd_origem_fonte_recursos_osc join osc.tb_dados_gerais e on a.id_osc = e.id_osc where a.bo_osc_ativa and a.id_osc not in (789809)) TO 'recursos.csv' WITH (FORMAT CSV, HEADER, DELIMITER ';', FORCE_QUOTE *);
